<style scoped>
.responsive-container {
  background: #fff5dc;
  position: relative;
  height: 0;
  padding-bottom: 52.35%;
  width: 500px;
}

@media (min-width: 600px) {
  .responsive-container {
    margin-left: calc((var(--width) - (500px + 4rem)) / 2 * -1);
    margin-right: calc((var(--width) - (500px + 4rem)) / 2 * -1);
    width: var(--width);
    height: calc(var(--width) * 0.5235);
  }
}

.details-thingy {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.burger-display {
  align-items: center;
  display: flex;
  justify-content: center;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  background: transparent;
  border: none;
  padding: 0;
}

.burger-diagram {
  width: 16.21%;
  height: auto;
  position: relative;
  top: 0.75%;
  z-index: 2;
  margin-top: 0;
}

.labels {
  display: block;
  margin: 0;
  opacity: 0;
  transition: 0.75s opacity ease-in-out;
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.labels.open {
  opacity: 1;
}

.click-for-details {
  position: relative;
  top: -200px;
  opacity: 1;
  transition: 0.75s opacity ease-in-out;
}

.click-for-details.hidden {
  opacity: 0;
}
</style>

<script>
import gsap from 'gsap';

export default {
  data() {
    return {
      clicked: false,
      open: false,
      animate: false,
    };
  },
  mounted() {
    gsap.to('.details-arrow', {
      y: 10,
      duration: 1,
      ease: 'sine.inOut',
      repeat: -1,
      yoyo: true,
    });

    this.animate = gsap.to('.burger-ingredient', {
      duration: 0.4,
      paused: true,
      ease: 'sine.out',
      y(_index, target) {
        const order = parseInt(target.dataset.ingredientOrder);
        const distance = (order - 5) * -25;

        return distance;
      },
      stagger(_index, target) {
        const order = parseInt(target.dataset.ingredientOrder);

        return 0.25 - (Math.abs(order - 5) / 5) * 0.25;
      },
    });
  },
  methods: {
    toggleBurgerSplode() {
      if (!this.open) {
        this.animate.play();
      } else {
        this.animate.reverse();
      }

      this.clicked = true;
      this.open = !this.open;
    },
  },
};
</script>
