<script>
import gsap from 'gsap';

export default {
  data() {
    return {
      clicked: false,
      open: false,
      animation: {},
    };
  },
  mounted() {
    gsap.to('.details-arrow', {
      y: 10,
      duration: 1,
      ease: 'sine.inOut',
      repeat: -1,
      yoyo: true,
    });

    this.animation.bob = gsap.to('.bobber', {
      paused: true,
      duration: 3,
      ease: 'sine.inOut',
      y: 'random(5, 15)',
      repeat: -1,
      yoyo: true,
    });

    this.animation.drift = gsap.to('.drifter', {
      paused: true,
      x: 'random(0, 20)',
      duration: 29,
      ease: 'sine.inOut',
      repeat: -1,
      yoyo: true,
    });

    this.animation.float = gsap.fromTo(
      '.floater',
      {
        transformOrigin: '50% 50%',
      },
      {
        paused: true,
        duration: 4,
        rotation: 12,
        ease: 'sine.inOut',
        repeat: -1,
        yoyo: true,
      },
    );
  },
  methods: {
    toggleBurgerFloat() {
      console.log('clicked');
      if (!this.open) {
        this.animation.bob.play();
        this.animation.drift.play();
        this.animation.float.play();
      } else {
        this.animation.bob.pause();
        this.animation.drift.pause();
        this.animation.float.pause();
      }

      this.clicked = true;
      this.open = !this.open;
    },
  },
};
</script>

